import { Component, OnInit } from '@angular/core';
import { Videogame } from 'src/app/models/juegos';

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html'
})

export class MainComponent implements OnInit {

  videojuegos: Videogame[];

  constructor() {
    this.videojuegos = listaVideogameDummy;
  }

  ngOnInit() {
  }

  comprobarDisponibilidad() {
    const idVideojuego = parseInt(this.idVideojuego.value);

    const videojuego = this.videojuegos.find(videojuego => videojuego.id === idVideojuego);

    if (videojuego.estatus === VideogameStatus.Available) {
      this.mensaje = 'El videojuego está disponible.';
    } else {
      this.mensaje = 'El videojuego no está disponible.';
    }
  }

}
