import { Component, OnInit } from '@angular/core';
import { JuegosService } from './juegos.service';

@Component({
  selector: 'app-catalogo',
  templateUrl: './catalogo.component.html',
  styleUrls: ['./catalogo.component.css'],
})
export class CatalogoComponent implements OnInit {
  constructor(private juegosService: JuegosService) {}

  ngOnInit() {}

  // Event handler for the "Comprar" button
  onComprarClick(event) {
    // Get the game ID from the button
    const gameId = event.target.id;

    // Make an HTTP request to check game availability
    this.juegosService.checkGameAvailability(gameId).subscribe(
      (isAvailable) => {
        // Display the message based on availability
        const messageElement = document.querySelector('.modal-message');
        messageElement.textContent = isAvailable
          ? 'El juego está disponible'
          : 'El juego no está disponible';
      },
      (error) => {
        console.error('Error checking game availability:', error);
      }
    );
  }
}
