import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { JuegosService } from './juegos.service';

@Component({
  selector: 'app-juegos',
  templateUrl: './juegos.component.html',
  styleUrls: ['./juegos.component.css'],
})
export class JuegosComponent implements OnInit {
  constructor(private juegosService: JuegosService) {}

  ngOnInit(): void {}

  // Event handler for the "Comprar" button
  onComprarClick(event) {
    // Get the game ID from the button
    const gameId = event.target.id;

    // Make an HTTP request to check game availability
    this.juegosService.checkGameAvailability(gameId).subscribe(
      (isAvailable) => {
        // Display the message based on availability
        document.querySelector('.modal-message').textContent = isAvailable
          ? 'El juego está disponible'
          : 'El juego no está disponible';
      },
      (error) => console.error(error)
    );
  }
}

@Injectable({
  providedIn: 'root',
})
export class JuegosService {
  constructor(private http: HttpClient) {}

  getJuegosListas(): Observable<Videogame[]> {
    const path = API_PATH + 'juegos';
    return this.http.get<Videogame[]>(path);
  }

  checkGameAvailability(gameId: number): Observable<boolean> {
    const path = `${API_PATH}juegos/${gameId}/availability`;
    return this.http.get<boolean>(path);
  }
}
