import { Component } from '@angular/core';

@Component({
  selector: 'app-inicio',
  templateUrl: './inicio.component.html',
  styleUrls: ['./inicio.component.scss']
})
export class InicioComponent {

}
<p>inicio works!</p>

<!DOCTYPE html>
<html>
<head>
  <title>Página de inicio de videojuegos</title>
  <style>
    body {
      font-family: Arial, sans-serif;
      background-color: #f2f2f2;
    }

    .container {
      max-width: 800px;
      margin: 0 auto;
      padding: 20px;
      background-color: #fff;
      border-radius: 5px;
      box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
    }

    h1 {
      text-align: center;
      color: #333;
    }

    p {
      text-align: center;
      color: #666;
    }

    .games-list {
      display: flex;
      flex-wrap: wrap;
      justify-content: center;
      margin-top: 20px;
    }

    .game-card {
      width: 200px;
      margin: 10px;
      padding: 10px;
      background-color: #f9f9f9;
      border-radius: 5px;
      box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
    }

    .game-card img {
      width: 100%;
      height: auto;
      border-radius: 5px;
    }

    .game-card h3 {
      margin-top: 10px;
      color: #333;
    }

    .game-card p {
      margin-top: 5px;
      color: #666;
    }
  </style>
</head>
<body>
  <div class="container">
    <h1>Bienvenido a nuestra página de inicio de videojuegos</h1>
    <p>Explora nuestra colección de juegos y diviértete</p>
    <div class="games-list">
      <div class="game-card">
        <img src="game1.jpg" alt="Game 1">
        <h3>Juego 1</h3>
        <p>Descripción del juego 1</p>
      </div>
      <div class="game-card">
        <img src="game2.jpg" alt="Game 2">
        <h3>Juego 2</h3>
        <p>Descripción del juego 2</p>
      </div>
      <div class="game-card">
        <img src="game3.jpg" alt="Game 3">
        <h3>Juego 3</h3>
        <p>Descripción del juego 3</p>
      </div>
    </div>
  </div>
</body>
</html>
