import { Component, OnInit } from '@angular/core';
import { JuegosService } from 'src/app/pages/services/juegos.service';
import { Videogame, listaVideogameDummy, VideogameStatus } from 'src/app/models/juegos';


@Component({
  selector: 'app-catalogo',
  templateUrl: './catalogo.component.html'
})

export class CatalogoComponent implements OnInit {

  // Event handler for the "Comprar" button
  onComprarClick(event: { target: { id: any; }; }) {
    // Get the game ID from the button
    const gameId = event.target.id;

    // Get the game from the list
    const juego = listaVideogameDummy.find((Videojuegogame) => Videogame.id === gameId);

    // Check the game availability
    const isAvailable = Videogame.estatus === VideogameStatus.Available;

    // Display the message based on availability
    const messageElement = document.querySelector('.modal-message');
    messageElement.textContent = isAvailable
      ? 'El juego está disponible'
      : 'El juego no está disponible';
  }

  ngOnInit() {}
}
